mapper.select:
select type_id as id, count(*) as commentCount from t_comment where comment_type=#{commentType} and type_id in
#{typeId}
group by type_id

select c.id, c.comment_content, c.create_time, u.avatar, u.nickname fromNickname ,u.id fromUid,u.web_site
from t_comment c
left join t_user u on c.from_uid = u.id



        type_id=#{conditionDTO.typeId}

    AND comment_type=#{conditionDTO.commentType}
    AND c.is_check=1
    AND parent_id IS NULL

ORDER BY c.id DESC
LIMIT #{limit},#{size}

select parent_id as comment_id, count(1) as reply_count from t_comment where is_check=1 and comment_type=#{commentType} and parent_id in
#{typeId}
group by parent_id
